    # ÊØè2ÁßíÂÆöÊó∂Ê£ÄÊü•‰∏ÄÊ¨°ÂèØÁî®‰∏≤Âè£
    def update_ports_loop(self):
        allocated_ports = []  # Â∑≤ÁªèÂàÜÈÖçÁöÑ‰∏≤Âè£

        while True:
            selected_ports_count = 0
            opened_ports_count = 0
            closed_ports_count = 0
            allocated_ports = []
            available_ports = self.get_available_ports()
            self.toggle_open_close_all()  # ÊØè2ÁßíÊõ¥Êñ∞‰∏ÄÊ¨°Ê£ÄÊü•ÊâÄÊúâ‰∏≤Âè£/ÂÖ≥Èó≠ÊâÄÊúâ‰∏≤Âè£ÊåâÈîÆÁä∂ÊÄÅ

            #   ÈÄöËøáÊéíÂ∫èÂ∞ÜÂèØÁî®‰∏≤Âè£ÂàÜÈÖçÁªôÂêÑ‰∏™‰∏≤Âè£
            for row in range(len(self.serial_rows)):
                available_ports = set(available_ports) - set(allocated_ports)
                if available_ports:
                    port_to_assign = sorted(list(available_ports))[0]  # ÈÄâÊã©ÊéíÂ∫èÂêéÁöÑÁ¨¨‰∏Ä‰∏™‰∏≤Âè£
                    self.serial_rows[row]['port_combobox'].set(port_to_assign)
                    allocated_ports.append(port_to_assign)
                    self.serial_rows[row]['port_combobox']['values'] = sorted(list(available_ports))
                    if self.serial_rows[row]['port_combobox'].get():
                        selected_ports_count += 1
                        if self.serial_rows[row]['port_status'] == 'Opened':
                            opened_ports_count += 1
                        else:
                            closed_ports_count += 1
                    self.serial_rows[row]['port_combobox']['values'] = []
                else:
                    self.serial_rows[row]['port_combobox']['values'] = []
                    self.serial_rows[row]['port_combobox'].set('')

            #  Â∑≤Âú®port_combobox ‰∏≠ÈÄâ‰∏≠‰∫Ü‰∏≤Âè£ÁöÑ‰∏≤Âè£ÊÄªÊï∞Èáè
            selected_ports_count = sum(
                bool(self.serial_rows[row]['port_combobox'].get()) for row in range(len(self.serial_rows)))
            time.sleep(1)

