    # ‰∏≤Âè£ÁÉßÂΩïÈÄªËæëÂèäÂÖ∂ÊñπÊ≥ï
    def burn_in_thread(self, row, port_var):
        """‰∏≤Âè£ÁÉßÂΩïÁ∫øÁ®ãÂÖ•Âè£ÔºàÊõ¥È≤ÅÊ£íÁöÑ YMODEM Êè°ÊâãÂ§ÑÁêÜÔºâ

        ‰øÆÂ§çÁÇπÔºö
        - ‰∏≤Âè£Êè°ÊâãÈò∂ÊÆµ‰∏ÄÂæã‰ΩøÁî® bytes Â§ÑÁêÜÔºåÈÅøÂÖç UTF-8 Ëß£Á†ÅÂØºËá¥Á∫øÁ®ãÂ¥©Ê∫É„ÄÇ
        - ÂÖÅËÆ∏Êé•Êî∂Á´ØÂú® 'C' ‰πãÂâçÂèëÈÄÅ‰ªªÊÑèÊùÇÂ≠óËäÇÔºåÂè™Ë¶ÅÂêéÁª≠Âá∫Áé∞ 'C' Âç≥ÂèØËøõÂÖ•ÂçáÁ∫ß„ÄÇ
        - Â¢ûÂä†È°∂Â±ÇÂºÇÂ∏∏ÂÖúÂ∫ïÔºåÈÅøÂÖçÁ∫øÁ®ãÂºÇÂ∏∏ÈÄÄÂá∫Âêé UI ÂÅáÊ≠ª„ÄÇ
        """

        def _restore_ui_fail(msg: str = "ÂçáÁ∫ßÂ§±Ë¥•ÔºÅ"):
            self.serial_rows[row]['close_button']['state'] = tk.NORMAL
            self.serial_rows[row]['flash_button']['state'] = tk.NORMAL
            self.serial_rows[row]['flash_status_label'].configure(fg='red', text=msg)
            self.progress_bars[row].configure(value=0)
            self.serial_rows[row]['percentage_label'].configure(text=f"{0}%")
            self.select_file_button['state'] = tk.NORMAL

        def _wait_for_cccc(ser, timeout_s: float = 5.0, read_chunk: int = 64):
            """Á≠âÂæÖ‰∏≤Âè£Êï∞ÊçÆÊµÅ‰∏≠Âá∫Áé∞ b'CCCC'ÔºàÂÖÅËÆ∏ÂâçÂØºÊùÇÂ≠óËäÇÔºâ„ÄÇ"""
            deadline = time.time() + timeout_s
            buf = bytearray()
            target = b"CCCC"

            while time.time() < deadline:
                chunk = ser.read(read_chunk)  # bytes
                if chunk:
                    buf.extend(chunk)

                    # ÊéßÂà∂ÁºìÂÜ≤Âå∫Â§ßÂ∞èÔºåÈÅøÂÖçÊó†ÈôêÂ¢ûÈïøÔºõ‰øùÁïôÂ∞æÈÉ®Áî®‰∫éË∑® chunk ÂåπÈÖç
                    if len(buf) > 2048:
                        del buf[:-64]

                    if target in buf:
                        return True, bytes(buf)
                else:
                    time.sleep(0.01)

            return False, bytes(buf)

        # ÁÉßÂΩïËøáÁ®ã‰∏≠Á¶ÅÁî®ÁÉßÂΩïÊåâÈîÆ,ÂÖ≥Èó≠‰∏≤Âè£ÊåâÈîÆÂíåÈÄâÊã©Êñá‰ª∂ÊåâÈîÆ,ÁÉßÂΩïÁä∂ÊÄÅÊòæÁ§∫Ê°ÜÊòæÁ§∫‚ÄòÁÉßÂΩï‰∏≠‚Äô
        self.serial_rows[row]['close_button']['state'] = tk.DISABLED
        self.serial_rows[row]['flash_button']['state'] = tk.DISABLED
        self.select_file_button['state'] = tk.DISABLED
        self.serial_rows[row]['flash_status_label'].configure(fg='blue', text="ÂçáÁ∫ß‰∏≠...")

        try:
            self.log.info(f"*** Á¨¨{row + 1}Ë°å‰∏≤Âè£ÁÉßÂΩïÁ∫øÁ®ãÂºÄÂßãÔºÅ")
            self.ser[row].port = port_var.get()

            if self.ser[row].isOpen():
                self.log.info(f"<<< Á¨¨{row + 1}Ë°å‰∏≤Âè£Â∑≤ÊâìÂºÄÔºÅ")
            else:
                self.log.info(f"<<< Á¨¨{row + 1}Ë°å‰∏≤Âè£Êú™ÊâìÂºÄÔºÅ")
                messagebox.showinfo("ÊèêÁ§∫", "‰∏≤Âè£Êú™ÊâìÂºÄÔºÅ")
                _restore_ui_fail("‰∏≤Âè£Êú™ÊâìÂºÄÔºÅ")
                return

            file = self.file_path_entry.get()
            print("<<< ÊâìÂºÄÊñá‰ª∂Ôºö", file)

            if len(file) <= 0:
                messagebox.showinfo("ÊèêÁ§∫", "ËØ∑ÈÄâÊã©Ê≠£Á°ÆÁöÑÊñá‰ª∂ÔºÅ")
                _restore_ui_fail("ËØ∑ÈÄâÊã©Ê≠£Á°ÆÁöÑÊñá‰ª∂ÔºÅ")
                return

            # Âª∫ËÆÆÔºöËøõÂÖ•ÂçáÁ∫ßÊè°ÊâãÂâçÊ∏ÖÁ©∫ËæìÂÖ•ÁºìÂÜ≤ÔºåÂáèÂ∞ëÂéÜÂè≤ÊÆãÁïôÂ≠óËäÇÂπ≤Êâ∞
            try:
                self.ser[row].reset_input_buffer()
            except Exception:
                pass

            # ÂÖàÂø´ÈÄüÊé¢Êµã‰∏ÄÊ¨°ÔºåÈÅøÂÖçÂ∑≤ÁªèÂ§Ñ‰∫éÊè°ÊâãÁä∂ÊÄÅÊó∂ÈáçÂ§çÂèëÈÄÅÂçáÁ∫ßÊåá‰ª§
            ok, raw = _wait_for_cccc(self.ser[row], timeout_s=0.5)
            if ok:
                print(f"<<< Á¨¨{row + 1}Ë°å‰∏≤Âè£ received 'CCCC' (pre)!")
            else:
                # ÂèëÈÄÅÂçáÁ∫ßÊåá‰ª§
                self.ser[row].write(b"$JS,UPGRADE\r\n")
                print(f">>> Á¨¨{row + 1}Ë°å‰∏≤Âè£ send upgrade instructions :'$JS,UPGRADE'ÔºÅ")

                # Á≠âÂæÖÊé•Êî∂Á´ØÂèëÈÄÅ CCCCÔºöÂÖÅËÆ∏ÂâçÈù¢Âá∫Áé∞ÊùÇÂ≠óËäÇÔºåÂæ™ÁéØÂ∞ùËØïÂ§öÊ¨°
                retry_count = 0
                ok = False
                while retry_count <= 10:
                    ok, raw = _wait_for_cccc(self.ser[row], timeout_s=1.0)
                    if raw:
                        # ÊâìÂç∞ÂéüÂßã bytes ÁöÑ hexÔºåÈÅøÂÖç decode Â¥©Ê∫É
                        print(f"<<< Á¨¨{row + 1}Ë°å‰∏≤Âè£ received raw: {raw.hex(' ')}\r\n")
                    if ok:
                        print(f"<<< Á¨¨{row + 1}Ë°å‰∏≤Âè£ received 'CCCC'ÔºÅ\r\n")
                        break

                    retry_count += 1

                if not ok:
                    self.log.info(f"*** Á¨¨{row + 1}Ë°å‰∏≤Âè£ flash failedÔºàÊú™Êî∂Âà∞ CCCCÔºâÔºÅ")
                    _restore_ui_fail("ÂçáÁ∫ßÂ§±Ë¥•ÔºàÊú™Êî∂Âà∞Êè°Êâã CÔºâÔºÅ")
                    return False

            # ËøõÂÖ• YMODEM ÂèëÈÄÅÈò∂ÊÆµ
            self.ymodem_send(file, row, lambda percentage: self.progress_bars[row].configure(value=percentage))

        except Exception as e:
            self.log.exception(f"*** Á¨¨{row + 1}Ë°å‰∏≤Âè£ÁÉßÂΩïÁ∫øÁ®ãÂºÇÂ∏∏ÈÄÄÂá∫: {e}")
            _restore_ui_fail("ÂçáÁ∫ßÂ§±Ë¥•ÔºàÁ∫øÁ®ãÂºÇÂ∏∏ÔºâÔºÅ")
            return False
    #   ÈÄöËøáymodemÂçèËÆÆÂèëÈÄÅÂçáÁ∫ßÊñá‰ª∂
    def ymodem_send(self, file_path, row, progress_callback):
        try:
            file_size = os.path.getsize(file_path)
            file_name = os.path.basename(file_path)
        except FileNotFoundError:
            self.log.info("<<< ÁÉßÂΩïÊñá‰ª∂Êú™ÊâæÂà∞!")
            return

        with open(file_path, 'rb') as file_stream:
            def callback(percentage):
                if percentage < 100 or percentage == 100:
                    self.root.after(0, progress_callback, percentage)
                    self.root.after(0, self.update_percentage_label, row, percentage)
                    self.log.info(f"<<<sent_percentage: {percentage}")
                elif percentage > 100:
                    percentage = 100
                    self.root.after(0, progress_callback, percentage)
                    self.root.after(0, self.update_percentage_label, row, percentage)
                    self.log.info(f"<<<sent_percentage: {percentage}")

            #   ÁÉßÂΩïÁä∂ÊÄÅÊ†áÂøó‰ΩçËøîÂõûÂèäÂà§Êñ≠
            def flash_status_callback(flash_status):
                #   flash_status‰∏∫1Ë°®Á§∫ÁÉßÂΩïÊàêÂäü
                if flash_status == 1:
                    self.flash_result = 1
                    self.log.info(f"*** Á¨¨{row + 1}Ë°å‰∏≤Âè£ÁÉßÂΩïÂÆåÊàêÔºÅ")
                    #   Âà§Êñ≠ÁÉßÂΩïÊòØÂê¶ÁªìÊùüÔºåÂ¶ÇÊûúÁÉßÂΩïÂÆåÊàêÔºåÂ∞±Êõ¥Êñ∞ÊåâÈîÆÁä∂ÊÄÅÔºöÊâìÂºÄÁÉßÂΩïÊåâÈîÆ,ÂÖ≥Èó≠‰∏≤Âè£ÊåâÈîÆÂíåÈÄâÊã©Êñá‰ª∂ÊåâÈîÆ
                    self.serial_rows[row]['close_button']['state'] = tk.NORMAL
                    self.serial_rows[row]['flash_button']['state'] = tk.NORMAL
                    self.serial_rows[row]['flash_status_label'].configure(fg='green', text="ÂçáÁ∫ßÊàêÂäüÔºÅ")
                    self.select_file_button['state'] = tk.NORMAL
                #   flash_status‰∏∫2Ë°®Á§∫ flash failed
                elif flash_status == 2:
                    self.flash_result = 1
                    self.log.info(f"*** Á¨¨{row + 1}Ë°å‰∏≤Âè£ flash failedÔºÅ")
                    #   Âà§Êñ≠ÁÉßÂΩïÊòØÂê¶ÁªìÊùüÔºåÂ¶ÇÊûú flash failedÔºåÂ∞±Êõ¥Êñ∞ÊåâÈîÆÁä∂ÊÄÅÔºöÊâìÂºÄÁÉßÂΩïÊåâÈîÆ,ÂÖ≥Èó≠‰∏≤Âè£ÊåâÈîÆÂíåÈÄâÊã©Êñá‰ª∂ÊåâÈîÆ
                    self.serial_rows[row]['close_button']['state'] = tk.NORMAL
                    self.serial_rows[row]['flash_button']['state'] = tk.NORMAL
                    self.serial_rows[row]['flash_status_label'].configure(fg='red', text="ÂçáÁ∫ßÂ§±Ë¥•ÔºÅ")
                    self.select_file_button['state'] = tk.NORMAL

            self.ymodem_sender = self.create_ymodem_sender(row)  # Ëé∑ÂèñÂΩìÂâçË°åÂØπÂ∫îÁöÑYMODEMÂÆû‰æã
            self.ymodem_sender.send(file_stream, file_name, file_size, callback=callback,
                                    flash_status_callback=flash_status_callback)

    #   Êõ¥Êñ∞ÁÉßÂΩïÁôæÂàÜÊØîÂèòÂåñ
    def update_percentage_label(self, row, percentage):
        self.serial_rows[row]['percentage_label'].configure(text=f"{percentage}%")

    # Ê†πÊçÆÊù°‰ª∂ÂêØÁî®ÊàñÁ¶ÅÁî®‰∏ÄÈîÆÁÉßÂΩïÊåâÈíÆ
    def update_flash_all_button_state(self):
        if self.opened_ports_count > 0 and self.file_path.get():
            self.flash_all_button['state'] = tk.NORMAL
        else:
            self.flash_all_button['state'] = tk.DISABLED

    #   ‰∏ÄÈîÆÁÉßÂΩïÊåâÈîÆ
    #   Â§ö‰∏™‰∏≤Âè£ÂêåÊó∂ÁÉßÂΩï
    def flash_all(self):
        # ‰∏ÄÈîÆÁÉßÂΩïÂÆåÊàêÂêéÊõ¥Êñ∞ÊåâÈîÆÁä∂ÊÄÅ
        self.opened_ports_count = 0
        self.update_flash_all_button_state()
        # ÂàõÂª∫‰∏Ä‰∏™Á∫øÁ®ãÂàóË°®ÔºåÁî®‰∫éÂ≠òÊîæÊØè‰∏™‰∏≤Âè£ÁöÑÁã¨Á´ãÁ∫øÁ®ã
        threads = []
        # ÂêåÊó∂ÂêØÂä®Â§ö‰∏™Á∫øÁ®ãËøõË°å‰∏ÄÈîÆÁÉßÂΩï
        if self.file_path.get():
            for row in range(len(self.serial_rows)):
                if self.ser[row].is_open:
                    # threading.Thread(target=self.burn_in_thread, args=(row, self.serial_rows[row]['port_combobox']),
                    #                  daemon=True).start()
                    self.flash(row, self.serial_rows[row]['port_combobox'])
        else:
            messagebox.showinfo("ÊèêÁ§∫", "ËØ∑ÂÖàÈÄâÊã©ÂçáÁ∫ßÊñá‰ª∂ÔºÅ")
            self.flash_all_button['state'] = tk.NORMAL

    #   ÁÉßÂΩïÊåâÈîÆ
    #   Âçï‰∏™‰∏≤Âè£ÁÉßÂΩï
    def flash(self, row, port_var):
        # ÁÉßÂΩïÈÄªËæë
        # ÂêØÂä®‰∏Ä‰∏™Á∫øÁ®ãÊâßË°åÁÉßÂΩï
        if self.file_path.get():
            self.ser[row].port = port_var.get()
            threading.Thread(target=self.burn_in_thread, args=(row, port_var), daemon=True).start()
        else:
            messagebox.showinfo("ÊèêÁ§∫", "ËØ∑ÂÖàÈÄâÊã©ÂçáÁ∫ßÊñá‰ª∂ÔºÅ")


